import React, {FC} from 'react';
import {Modal,Box} from "@mui/material";
//defining props
type Props = {
    open: boolean;
    setOpen:(open:boolean) => void;
    activeItem: any;
    component: any;
    setRoute?:(route:string) => void;
}
//creating page
const CustomModal:FC<Props> = ({open,setOpen,setRoute,component:Component}) => {
  return (
    <Modal open={open} onClose={() => setOpen(false)} aria-labelledby="modal-modal-title" aria-describedby="modal-modal-description">
        <Box className="relative left-[50%] translate-x-[-50%] top-[50%] translate-y-[-50%] 800px:w-[450px] 400px:w-[380px] w-[250px] bg-white dark:bg-slate-900 rounded-[8px] shadow p-4 outline-none">
            <Component setOpen={setOpen} setRoute={setRoute}/>
        </Box>
    </Modal>
  )
}
export default CustomModal;
